const waveFormat = AudioStreamFormat.getWaveFormatPCM(8000, 8, 2);
const audioStreamCallback = {
                        read: (dataBuffer: any) => {
                            
                            const bytesRead = Math.min(dataBuffer.byteLength, frame.audio.data.length);
                
                            // Copy the binary data to the dataBuffer
                            for (let i = 0; i < bytesRead; i++) {
