    @Test
    public void testMyIssue() {
        qm.createConfigProperty(ConfigPropertyConstants.SCANNER_INTERNAL_FUZZY_ENABLED.getGroupName(), ConfigPropertyConstants.SCANNER_INTERNAL_FUZZY_ENABLED.getPropertyName(),"true", IConfigProperty.PropertyType.BOOLEAN, "");
        var project = new Project();
        project.setName("acme-app");
        project = qm.createProject(project, Collections.emptyList(), false);
        var component = new Component();
        component.setProject(project);
        component.setName("util-linux-setarch");
        component.setVersion("2.37.4");
        component.setCpe("cpe:2.3:a:*:util-linux-setarch:2.37.4:*:*:*:*:*:*:*");
        component = qm.createComponent(component, false);

        var vulnerableSoftware = new VulnerableSoftware();
        vulnerableSoftware.setCpe23("cpe:2.3:a:pascom_cloud_phone_system:*:*:*:*:*:*:*:*:*");
        vulnerableSoftware.setEdition("*");
        vulnerableSoftware.setPart("a");
        vulnerableSoftware.setProduct("*");
        vulnerableSoftware.setVersion("*");
        vulnerableSoftware.setVendor("pascom_cloud_phone_system");
        vulnerableSoftware.setVersionEndIncluding("7.19");
        vulnerableSoftware.setVulnerable(true);
        vulnerableSoftware = qm.persist(vulnerableSoftware);

        var vulnerability = new Vulnerability();
        vulnerability.setVulnId("CVE-2021-45967");
        vulnerability.setSource(Vulnerability.Source.NVD);
        vulnerability.setVulnerableSoftware(List.of(vulnerableSoftware));
        qm.createVulnerability(vulnerability, false);

        new InternalAnalysisTask().analyze(List.of(component));

        final PaginatedResult vulnerabilities = qm.getVulnerabilities(component);
        assertThat(vulnerabilities.getTotal()).isEqualTo(1);
        assertThat(vulnerabilities.getList(Vulnerability.class).get(0).getVulnId()).isEqualTo("CVE-2021-45967");
    }
