+      );
+      pkgVersMap.set(pkgName, pkgVersions);
+    }
+
+    let versions;
+    const filteredVers = pkgVersions
+      .filter((v) => semver.satisfies(v, include))
+      .filter((v) => !exclude || !semver.satisfies(v, exclude))
+      .map(semver.parse);
