const map = L.map('map', {
    preferCanvas: true
}).setView([0, 0], 1);

const osm = L.tileLayer('https://tile.openstreetmap.org/{z}/{x}/{y}.png', {
    maxZoom: 24,
    maxNativeZoom: 19,
    attribution: '&copy; <a href="http://www.openstreetmap.org/copyright">OpenStreetMap</a>'
}).addTo(map);

const baseLayers = {
    'OpenStreetMap': osm
};

const layerControl = new L.Control.Layers(baseLayers).addTo(map);

function createLayerControlWithButton(layer, name) {
    let buttonID = name.replace(/\s/g, '') + 'Button';
    const buttonHTML = "<button id='" + buttonID + "'>Go To " + name + "</button> " + name;

    layerControl.addOverlay(layer, buttonHTML);

    // Add a click event to the button
    let button = document.getElementById(buttonID);
    L.DomEvent.on(button, 'click', function () {
        map.fitBounds(layer.getBounds());
    });
}

let markerGroupA = L.featureGroup();
let latLng1 = L.latLng(0, 0);
let marker1 = L.marker(latLng1);
let latLng2 = L.latLng(10, 100);
let marker2 = L.marker(latLng2);
marker1.addTo(map);
markerGroupA.addLayer(marker1);
marker2.addTo(map);
markerGroupA.addLayer(marker2);
createLayerControlWithButton(markerGroupA, 'Marker Group A');

let markerGroupB = L.featureGroup();
let latLng3 = L.latLng(0, -100);
let marker3 = L.marker(latLng3);
let latLng4 = L.latLng(-10, -100);
let marker4 = L.marker(latLng4);
marker3.addTo(map);
markerGroupB.addLayer(marker3);
marker4.addTo(map);
markerGroupB.addLayer(marker4);
createLayerControlWithButton(markerGroupB, 'Marker Group B');


