+                            (HealthStatusUpdate::Running, SshTunnelStatus::Errored(e)) => {
+                                status_to_emit.update = HealthStatusUpdate::Stalled {
+                                    error: e.to_string(),
+                                    hint: None,
+                                    should_halt: false
+                                };
+                            }
