  asd.addListener('lookup', (...args) => console.log('lookup', ...args))
  asd.addListener('ready', (...args) => console.log('ready', ...args))
  asd.addListener('timeout', (...args) => console.log('timeout', ...args))
  setTimeout(() => {
    asd.destroy();
    // asd.drop()
