package com.hhdev.archway.filters;

import static org.springframework.cloud.gateway.support.GatewayToStringStyler.filterToStringCreator;
import static org.springframework.cloud.gateway.support.ServerWebExchangeUtils.GATEWAY_REQUEST_URL_ATTR;
import static org.springframework.cloud.gateway.support.ServerWebExchangeUtils.GATEWAY_ORIGINAL_REQUEST_URL_ATTR;
import static org.springframework.cloud.gateway.support.ServerWebExchangeUtils.addOriginalRequestUrl;

import java.net.URI;
import java.nio.file.Files;
import java.nio.file.Path;
import java.util.LinkedHashSet;
import java.util.regex.Matcher;
import java.util.regex.Pattern;

import org.apache.commons.logging.Log;
import org.apache.commons.logging.LogFactory;
import org.springframework.cloud.gateway.filter.GatewayFilter;
import org.springframework.cloud.gateway.filter.GatewayFilterChain;
import org.springframework.cloud.gateway.filter.factory.AbstractGatewayFilterFactory;
import org.springframework.core.io.ClassPathResource;
import org.springframework.core.io.Resource;
import org.springframework.core.io.buffer.DataBuffer;
import org.springframework.core.io.buffer.DataBufferUtils;
import org.springframework.http.HttpHeaders;
import org.springframework.http.HttpStatus;
import org.springframework.http.MediaType;
import org.springframework.http.server.reactive.ServerHttpRequest;
import org.springframework.http.server.reactive.ServerHttpResponse;
import org.springframework.stereotype.Component;
import org.springframework.web.server.ServerWebExchange;

import lombok.Data;
import reactor.core.publisher.Mono;


@Component()
public class ClasspathFileFilter extends AbstractGatewayFilterFactory<ClasspathFileFilter.ClasspathConfig> {

	private static final Log log = LogFactory.getLog(ClasspathFileFilter.class);

    public ClasspathFileFilter() {
        super(ClasspathConfig.class);
    }
    @Override
	public GatewayFilter apply(ClasspathConfig config) {
        String classpath = config.getPath();
		return new GatewayFilter() {
			@Override
			public Mono<Void> filter(ServerWebExchange exchange, GatewayFilterChain chain) {
                if (exchange.getResponse().isCommitted()) {
                    return Mono.empty();
                }
                LinkedHashSet<URI> oriRequestUrls = exchange.getRequiredAttribute(GATEWAY_ORIGINAL_REQUEST_URL_ATTR);
                String oriRequestUrl = oriRequestUrls.stream().map((URI uri) -> uri.getPath()).findFirst().orElse("");
                ServerHttpRequest req = exchange.getRequest();
				addOriginalRequestUrl(exchange, req.getURI());
                URI requestUrl = exchange.getRequiredAttribute(GATEWAY_REQUEST_URL_ATTR);
                String root = oriRequestUrl.replace(requestUrl.getPath(), "");
                final ServerHttpResponse response = exchange.getResponse();
                return tryExactTarget(classpath, requestUrl.getPath(), response)
                    // .switchIfEmpty(Mono.defer(() -> redirectTo(response, root+"/")))
                    .switchIfEmpty(Mono.defer(() -> tryFileTarget(classpath, requestUrl.getPath(), response)))
                    .switchIfEmpty(Mono.defer(() -> tryExactTarget(classpath, "/index.html", response)))
                    .switchIfEmpty(Mono.defer(() ->chain.filter(exchange)));
			}

			@Override
			public String toString() {
				return filterToStringCreator(ClasspathFileFilter.this).append(classpath).toString();
			}
		};
	}

    private Mono<Void> redirectTo(ServerHttpResponse response, String path) {
        if (!response.isCommitted()) {
            response.setRawStatusCode(301);
            response.getHeaders().set(HttpHeaders.LOCATION, path);
            return response.setComplete();
        }
        return Mono.empty();
    }

    private Mono<Void> tryFileTarget(String classpath, String filepath, ServerHttpResponse response) {
        if (!response.isCommitted()) {
            String regex = ".*(/.*?)$";
            Pattern pattern = Pattern.compile(regex);
            Matcher matcher = pattern.matcher(filepath);
            if (matcher.find()) {
                String file = matcher.group(1);
                String target = buildClassPath(classpath, file);
                return tryTarget(target, response);
            }
        }
        return Mono.empty();
    }

    private Mono<Void> tryExactTarget(String classpath, String filepath, ServerHttpResponse response) {
        if (!response.isCommitted()) {
            String target = buildClassPath(classpath, filepath);
            return tryTarget(target, response);
        }
        return Mono.empty();
    }

    private Mono<Void> tryTarget(String target, ServerHttpResponse response) {
        if (!response.isCommitted()) {
            Resource resource = new ClassPathResource(target);
            if (resource.exists()) {
                response.setStatusCode(HttpStatus.OK);
                response.getHeaders().setContentType(getContentType(resource));
                return response.writeWith(DataBufferUtils.read(resource, response.bufferFactory(), 1024)).doOnDiscard(DataBuffer.class, DataBufferUtils::release);
            }
        }
        return Mono.empty();
    }

    private String buildClassPath(String uri, String path) {
        uri = uri.replaceAll("^classpath:/", "").replaceAll("/$", "");
        if (path.equals("/")) {
            return uri + "/index.html";
        } else {
            return uri + path;
        }
    }

    private MediaType getContentType(Resource resource) {
        try {
            Path filePath = resource.getFile().toPath();
            String contentType =  Files.probeContentType(filePath);
            if (contentType != null) {
                return MediaType.parseMediaType(contentType);
            }
            if (filePath.getFileName().endsWith("map")) {
                return MediaType.parseMediaType("application/json map");
            }
        } catch (Exception e) {
        }
        return MediaType.parseMediaType(MediaType.APPLICATION_OCTET_STREAM_VALUE);
    }

    @Data
    public static class ClasspathConfig {
        String path;
    }

}

