+};
+
+std::vector<Status> files;
+tr_file_index_t current_index;
+std::vector<std::string> errors;
+bool print_ok_files;
+bool unsorted;
+tr_torrent_metainfo const* metainfo;
+
+void print_file(Status const& status)
