+    for (auto const& file_error : status.errors)
+    {
+        fmt::print("    {}\n", file_error);
+    }
+}
+
+void verify_callback(tr_file_index_t index, bool ok, std::string error)
+{
+    if (index != current_index)
+    {
+        if (!errors.empty())
+        {
+            Status status;
+            status.index = current_index;
+            status.ok = false;
+            status.errors = errors;
+            if (unsorted)
+            {
+                print_file(status);
+            }
+            else
+            {
+                files.emplace_back(status);
+            }
+            errors.clear();
+        }
+        current_index = index;
+    }
+
+    if (ok)
+    {
+        if (print_ok_files)
+        {
+            Status status;
+            status.index = index;
+            status.ok = true;
+            if (unsorted)
+            {
+                print_file(status);
+            }
+            else
+            {
+                files.emplace_back(status);
+            }
+        }
+    }
+    else
+    {
+        errors.push_back(error);
+    }
+}
+
